import Paper from "@material-ui/core/Paper";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import React from "react";
import {
  Backdrop,
  CircularProgress,
  Grid,
  Typography,
  TextField,
  InputAdornment,
} from "@material-ui/core";
import moment from "moment";
import SearchIcon from "@material-ui/icons/Search";
import toastr from "toastr";
import apiURL from "../../constant/constant";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: "#3f51b5",
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {},
  },
}))(TableRow);

const useStyles = makeStyles((theme) => ({
  backdrop: {
    zIndex: theme.zIndex.drawer + 1,
    color: "#fff",
  },
}));

function SimpleBackdrop(open) {
  const classes = useStyles();
  return (
    <Backdrop className={classes.backdrop} open={open.open}>
      <CircularProgress color="inherit" />
    </Backdrop>
  );
}

export default class Transaction extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      transactions: null,
      isOpen: false,
      dataSearch: null,
      searchValue: "",
    };
  }

  componentDidMount() {
    const proxyurl = apiURL.proxyUrl;
    let url = `${apiURL.baseUrl}/Transaction`;
    this.setState({ isOpen: true });
    toastr.options = {
      closeButton: false,
      debug: false,
      newestOnTop: false,
      progressBar: false,
      positionClass: "toast-top-right",
      preventDuplicates: false,
      onclick: null,
      showDuration: "300",
      hideDuration: "1000",
      timeOut: "3000",
      extendedTimeOut: "1000",
      showEasing: "swing",
      hideEasing: "linear",
      showMethod: "fadeIn",
      hideMethod: "fadeOut",
    };
    fetch(proxyurl + url)
      .then((response) => response.json())
      .then((data) => this.setState({ transactions: data, isOpen: false }))
      .catch(() => {
        this.setState({
          isOpen: false,
        });
        toastr.error(
          "There are some error had been happen. Please try in another time.",
          "Fail"
        );
      });
  }
  handleSearch = (event) => {
    const { transactions } = this.state;
    const value = event.target.value;
    this.setState({
      searchValue: value,
      dataSearch:
        transactions &&
        transactions.filter(function (item) {
          return item.id.toLowerCase().includes(value.toLowerCase());
        }),
    });
  };

  render() {
    const { transactions, isOpen, dataSearch, searchValue } = this.state;
    return (
      <TableContainer component={Paper} style={{ maxHeight: "30rem" }}>
        <SimpleBackdrop open={isOpen} />
        <Grid
          container
          xs={12}
          alignItems="center"
          style={{ padding: "0.5rem", border: "1px solid blue" }}
        >
          <Grid item xs={4} style={{ padding: "0 2rem" }}>
            <Typography variant="h5" color="primary">
              Transaction
            </Typography>
          </Grid>
          <Grid item xs={8} container justify="flex-end">
            <TextField
              label="Search"
              variant="outlined"
              margin="dense"
              onChange={this.handleSearch}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
        </Grid>
        <Table stickyHeader aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>Id</StyledTableCell>
              <StyledTableCell align="center">Transaction Time</StyledTableCell>
              <StyledTableCell align="center">Status</StyledTableCell>
              <StyledTableCell align="center">Shares Quantity</StyledTableCell>
              <StyledTableCell align="center">Time Approve</StyledTableCell>
              <StyledTableCell align="center">Series Id</StyledTableCell>
              <StyledTableCell align="center">
                Transaction TypeId
              </StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {searchValue === "" ? (
              transactions &&
              transactions.map((row, index) => (
                <StyledTableRow key={index}>
                  <StyledTableCell component="th" scope="row">
                    {row.id}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.transactionTime}
                  </StyledTableCell>
                  <StyledTableCell align="center">{row.status}</StyledTableCell>
                  <StyledTableCell align="center">
                    {row.sharesQuantity}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.timeApprove &&
                      moment(row.timeApprove).format("YYYY-MM-DD")}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.seriesId}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.transactionTypeId}
                  </StyledTableCell>
                </StyledTableRow>
              ))
            ) : dataSearch && dataSearch.length > 0 ? (
              dataSearch.map((row, index) => (
                <StyledTableRow key={index}>
                  <StyledTableCell component="th" scope="row">
                    {row.id}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.transactionTime}
                  </StyledTableCell>
                  <StyledTableCell align="center">{row.status}</StyledTableCell>
                  <StyledTableCell align="center">
                    {row.sharesQuantity}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.timeApprove &&
                      moment(row.timeApprove).format("YYYY-MM-DD")}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.seriesId}
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    {row.transactionTypeId}
                  </StyledTableCell>
                </StyledTableRow>
              ))
            ) : (
              <StyledTableRow>
                <StyledTableCell align="left">
                  No record is matched.
                </StyledTableCell>
              </StyledTableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
    );
  }
}
